mapper.select:
SELECT a.reportno as id, a.div, a.reportno, a.report_sts, a.tran_date, to_char(a.visit_dt,'mm/dd/yyyy hh24:mi:ss') as visit_dt, a.technician, (select name from mscarsms where div = #{div} and car_id = a.ev_car) as ev_car, a.cust_cd, a.bill_to_cust, a.beg_ptc, a.end_ptc, a.beg_noc, a.end_noc, a.site_cd, a.site_name, a.address, a.city, a.state, a.zip, a.wt_ci1, a.wt_co1, a.wt_ci2, a.wt_co2, a.wt_ci3, a.wt_co3, a.wt_ci4, a.wt_co4, a.wt_ci5, a.wt_co5, nvl(((a.wt_co1 - a.wt_ci1) * 1440), 0) as wt1, nvl(((a.wt_co2 - a.wt_ci2) * 1440), 0) as wt2, nvl(((a.wt_co3 - a.wt_ci3) * 1440), 0) as wt3, nvl(((a.wt_co4 - a.wt_ci4) * 1440), 0) as wt4, nvl(((a.wt_co5 - a.wt_ci5) * 1440), 0) as wt5, a.hotel_night, a.hotel_amt, a.mileage, a.rental_amt, a.others_amt, a.fiscal_period, a.fs_no, a.work_type, a.invoice_no, a.create_id, a.create_dt, a.update_id, a.update_dt, a.confirm_id, a.confirm_dt, a.modified, a.mod_yn, a.mod_dt, a.reviewd, a.review_yn, a.review_dt, a.email_fg, (select fname || ' ' || lname from msuserms where div = #{div} and user_id = a.create_id) as prepared, (select fname || ' ' || lname from msuserms where div = #{div} and user_id = a.reviewd) as approved FROM sitevrhd a WHERE a.div = #{div}
AND a.visit_dt >= to_date( #{visit_fr}, 'MM/DD/YYYY')
AND a.visit_dt <= to_date( #{visit_to}, 'MM/DD/YYYY')
AND a.report_sts = #{report_sts}
AND a.reportno = #{reportno}
AND a.fs_no = #{wo_no}
ORDER BY a.visit_dt DESC

mapper.update:

     update sitevrhd
        set report_sts=#{report_sts} ,
            visit_dt=#{visit_dt}
      where div=#{div} and reportno=#{reportno}
